import { Col, Container, Row, Table } from "react-bootstrap";
import "./App.css";
import { TaskForm } from "./components/TaskForm";

import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { fetchTasks } from "./helper/axiosHelper";

function App() {
  const [tasksList, setTasksList] = useState([]);

  const getTaskList = async ()=>{
    const {status, taskList} = await fetchTasks();

    if(status === "success" && taskList.length){
    setTasksList(taskList);
    }
  };
     console.log(tasksList);



  return (
    <div className="wrapper">
      <Container>
        <Row>
          <Col className="mt-5 text-center fs-1 mb-2"><h1>Task Mgmt</h1></Col>
        </Row>

        {/* form  */}
        <TaskForm getTaskList={getTaskList} />

        {/* task display table  */}

        <Row className="mt-5">
          <Col>
            <h3 className="text-center">Entry List</h3>
            <hr />
            <Table striped bordered hover className="bg-transparent">
              <tbody>
                <tr>
                  <td>1</td>
                  <td>Mark</td>
                  <td>Otto</td>
                  <td>@mdo</td>
                </tr>
                <tr>
                  <td>2</td>
                  <td>Jacob</td>
                  <td>Thornton</td>
                  <td>@fat</td>
                </tr>
                <tr>
                  <td>3</td>
                  <td colSpan={2}>Larry the Bird</td>
                  <td>@twitter</td>
                </tr>
              </tbody>
            </Table>
          </Col>
          <Col>
            <h3 className="text-center">Bad List</h3>
            <hr />{" "}
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default App;
